import './App.css';
import Box from './component/Box';

//1. 박스2개 그리기
//박스 ( 타이틀, 사진, 결과값 )
//2. 가위,바위,보 버튼이 존재한다.
//3. 버튼을 클릭하게 되면 클릭한 값이 박스에 보인다. 
//4. 컴퓨터는 랜덤하게 아이템이 선택된다. 
//5. 3,4번의 결과로 누가 이겼는지 승패를 따진다.
//6. 디테일하게 들어가는 작업으로는 승패에 따라서 테두리 색상이 변경된다. ( 이기면 - 초록 ,지면 - 빨강 , 비기면 -검정색 )

function App() {
  return(
    <div>
  <div className='main'>
<Box title="you"/>
<Box title="computer"/>
  </div>

  //컴포넌트는 반드시 하나를 return 해야하니까 하나로 감싸야지 에러가 발생하지 않는다.
  <div>
    <button>가위</button>
    <button>바위</button>
    <button>보</button>
  </div>
  </div>
  );
}

export default App;
